/* ------------------------------------------------------------------ */
/* Board.jsx                                                          */
/* Author: John Hart                                                  */
/* ------------------------------------------------------------------ */

import React from "react";

import ChessboardSquare from './ChessboardSquare'
import ChessboardKnight from './ChessboardKnight'

const renderSquare = new function(x, y, [knightX, knightY])
    {
      const black = (x + y) % 2 === 1
      const isKnightHere = knightX === x && knightY === y
      const piece = isKnightHere ? <ChessboardKnight /> : null

      return <ChessboardSquare black={black}>{piece}</ChessboardSquare>
    };

export default function Board({ knightPosition }) {
  return (
    <div
      style={{
        width: '100%',
        height: '100%'
      }}
    >
      {renderSquare(0, 0, knightPosition)}
      {renderSquare(1, 0, knightPosition)}
      {renderSquare(2, 0, knightPosition)}
    </div>
  )
}
