/* ------------------------------------------------------------------ */
/* Square.jsx                                                         */
/* Author: John Hart                                                  */
/* ------------------------------------------------------------------ */

import React from 'react';

export default function Square({index, children}) {
    const color = index % 2 === 1;
    const fill = color ? 'black' : 'orange';
    const stroke = color ? 'orange' : 'black';

    return (
        <div
            style={{
                background: fill,
                color: stroke,
                width: 100,
                height: 100
            }}
        >
            {children}
        </div>
    )
}
